package com.Dibyanshu.ControlStatements;

public class ControlStatementsIntro {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int age = 25;
		
		if(age>=16)
		{
			System.out.println("You can drive !"+'\n');
		}
		else
		{
			System.out.println("You can not drive yet !"+'\n');
		}
		for(int i =1; i<=age; i++)
		{
			System.out.println("Happy "+i+" Birthday !!");
		}

	}// end main

}
